import { providers } from "ethers"
import React, { useContext, useEffect, useState } from "react"
import WalletConnectProvider from "@walletconnect/web3-provider"
import CoinbaseWalletSDK from "@coinbase/wallet-sdk"
import Web3Modal from "web3modal"
import dotenv from 'dotenv'
dotenv.config()

const Web3Context = React.createContext({
  connect: () => Promise.resolve({}),
  clearCachedProvider: () => Promise.resolve({})
})

const useWeb3 = () => useContext(Web3Context)

const Web3Provider = ({ children }) => {
  const [web3Modal, setWeb3Modal] = useState()
  const [web3Resources, setWeb3Resources] = useState()

  useEffect(() => {
    const w3m = new Web3Modal({
      network: "ropsten",
      cacheProvider: false,
      providerOptions: {
        injected: {
          display: {
            logo:
              "data:image/png;base64,",
            name: "Metamask",
            description: "Connect with the provider in your Browser"
          },
          package: null
        },
        walletconnect: {
          package: WalletConnectProvider,
          options: {
            infuraId: process.env.INFURA_ID
          }
        },
        coinbasewallet: {
          package: CoinbaseWalletSDK,
          options: {
            appName: "Alfred",
            infuraId: process.env.INFURA_ID
          }
        }
      }
    })
    setWeb3Modal(w3m)
  }, [])

  const clearCachedProvider = async () => {
    if (!web3Modal) return
    await web3Modal.clearCachedProvider()
  }

  const connect = async () => {
    if (!web3Modal) return
    const instance = await web3Modal.connect()
    const _provider = new providers.Web3Provider(instance)
    const signer = await _provider.getSigner()
    const account = await signer.getAddress()

    _provider.on("accountsChanged", accounts => {
      console.log(accounts)
    })

    // Subscribe to chainId change
    _provider.on("chainChanged", chainId => {
      console.log(chainId)
    })

    // Subscribe to provider connection
    _provider.on("connect", info => {
      console.log(info)
    })

    // Subscribe to provider disconnection
    _provider.on("disconnect", error => {
      console.log(error)
    })

    setWeb3Resources({
      instance,
      provider: _provider,
      signer,
      account
    })
  }

  return (
    <Web3Context.Provider value={{ ...web3Resources, connect, clearCachedProvider }}>
      {children}
    </Web3Context.Provider>
  )
}

export { Web3Provider, useWeb3 }